import React, { useState } from 'react';

const FileInputComponent = () => {
  const [filePath, setFilePath] = useState('');

  const handleFileChange = (e) => {
    const file = e.target.files[0];

    if (file && file.name.endsWith('.c')) {
      // Assuming you want to store the path in the variable
      setFilePath(file.path);
      // You can also do something with the file itself if needed
      // For example, you can read the contents of the file using FileReader
      // const reader = new FileReader();
      // reader.onload = (event) => {
      //   const fileContent = event.target.result;
      //   console.log(fileContent);
      // };
      // reader.readAsText(file);
    } else {
      alert('Please select a valid .c file');
    }
  };

  return (
    <div>
      <label htmlFor="fileInput">Select a .c file:</label>
      <input
        type="file"
        id="fileInput"
        accept=".c"
        onChange={handleFileChange}
      />
      <p>Selected File Path: {filePath}</p>
    </div>
  );
};

export default FileInputComponent;
